var ArticleSchema = mongoose.Schema({
    id: Number,
    headline: String,
    biline: String,
    creditline: String,
    source: String,
    section: String,
    URL: String,
    body: String,
    keywords: [Number]
});

var UserSchema = mongoose.Schema({
    username: String,
    fullname: String,
    fullname: String,
    hometown: String,
    current_location: String,
    gender: String,
    languages: [String],
    work: [String],
    keywords: [Number]

});

var User = mongoose.model("User", UserSchema);
var Article = mongoose.model("Article", ArticleSchema);



