exports.userReadArticle = function(userID, articleID)
{
    User.findOne({username: userID}, function(err, user){
	if(err) throw err;
	if(user)
	{
	    Article.findOne({id: articleID}, function(err, article){
		for(var key in article.keywords)
		{
		    user.keywords[key] += article.keywords[key];
		}
	    });
	}
    });
};

